import { useEffect, useState } from "react";
import _ from "lodash";
import Column from "../Column/Column";
import { mockData } from "../../actions/mockData";

import "./BoardContent.scss";
import { isCompositeComponentWithType } from "react-dom/test-utils";
import { stringify } from "querystring";

const BoardContent = () => {
	const [board, setBoard] = useState({});
	const [columns, setColumns] =
		useState <
		Array <
		{
			id: string,
			boardId: string,
			title: string,
			cardOrder: Array<{string}>,
			cards: Array<{
				id: string,
				boardId: string,
				columnId: string,
				title: string,
				image: string
			}>
		} >>
			[];

	useEffect(() => {
		const boardMockData = mockData.boards.find((item) => item.id === "board-1");
		if (boardMockData) {
			setBoard(boardMockData);
			setColumns(boardMockData.columns);
		}
	}, []);

	if (_.isEmpty(board)) {
		return (
			<>
				<div className='not-found'>Board not found</div>
			</>
		);
	}

	console.log(columns);

	return (
		<>
			<div className='trelloColumns'>
				{columns &&
					columns.length > 0 &&
					columns.map((column, index) => {
						return <Column key={column.id} column={column} />;
					})}
				<Column />
			</div>
		</>
	);
};

export default BoardContent;
