interface Card {
	id: string;
	boardId: string;
	columnId: string;
	title: string;
	image: string | null;
}

interface Category {
	id: string;
	boardId: string;
	title: string;
	cardOrder: Array<string>;
	cards: Array<Card>;
}

export const mapOrder = (
	array: Category[],
	order: string[],
	key: keyof Category
) => {
	array.sort(
		(a: Category, b: Category) => order.indexOf(a[key]) - order.indexOf(b[key])
	);
	return array;
};
