import Card from "../Card/Card";
import { mapCardOrder } from "../../utilities/sort";
import { Container, Draggable } from "react-smooth-dnd";

import "./Column.scss";

interface CardInfo {
	id: string;
	boardId: string;
	columnId: string;
	title: string;
	image: string | null;
}

interface Category {
	id: string;
	boardId: string;
	title: string;
	cardOrder: Array<string>;
	cards: Array<CardInfo>;
}

const Column = (props: { column: Category }) => {
	const { column } = props;
	const cards = mapCardOrder(column.cards, column.cardOrder, "id");

	return (
		<>
			<div className='column'>
				<header className='column-drag-handle'>{column.title}</header>
				<div className='card-list'>
					<Container
						{...column.props}
						groupName='col'
						onDragStart={(e) => console.log("drag started", e)}
						onDragEnd={(e) => console.log("drag end", e)}
						onDrop={(e) => this.onCardDrop(column.id, e)}
						getChildPayload={(index) => this.getCardPayload(column.id, index)}
						dragClass='card-ghost'
						dropClass='card-ghost-drop'
						onDragEnter={() => {
							console.log("drag enter:", column.id);
						}}
						onDragLeave={() => {
							console.log("drag leave:", column.id);
						}}
						onDropReady={(p) => console.log("Drop ready: ", p)}
						dropPlaceholder={{
							animationDuration: 150,
							showOnTop: true,
							className: "drop-preview",
						}}
						dropPlaceholderAnimationDuration={200}>
						{cards &&
							cards.length > 0 &&
							cards.map((card, index) => {
								return <Card key={card.id} card={card} />;
							})}
					</Container>
				</div>
				<footer>+ New Card</footer>
			</div>
		</>
	);
};

export default Column;
